class Solution {
public:

    const int MOD=1000000007;

    int solve(vector<int>& nums, int n, int i, int prev_ai, int prev_bi, vector<vector<vector<int>>>& memo) {
        if (i == n) {
            return 1;
        }

        if (memo[i][prev_ai][prev_bi] != -1) {
            return memo[i][prev_ai][prev_bi];
        }

        int count = 0;
        for (int j = 0; j <= nums[i]; ++j) {
            int ai = j;
            int bi = nums[i] - j;
            if (i == 0 || (prev_ai <= ai && prev_bi >= bi)) {
                count = (count+(solve(nums, n, i + 1, ai, bi, memo)%MOD))%MOD;
            }
        }


        memo[i][prev_ai][prev_bi] = count;
        return count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int max_val = *max_element(nums.begin(), nums.end());

        vector<vector<vector<int>>> memo(n, vector<vector<int>>(max_val + 1, vector<int>(max_val + 1, -1)));

        return solve(nums, n, 0, 0, 0, memo);
    }
};
