using LL = long long;
class Solution {
    LL dp[2005][51];
    LL M=1e9+7;
public:
    int countOfPairs(vector<int>& nums) 
    {
        int n = nums.size();        
        nums.insert(nums.begin(), 0);
        for (int j=0; j<=nums[1]; j++)
            dp[1][j] = 1;

        for (int i=2; i<=n; i++)
            for (int j=0; j<=nums[i]; j++)
            {                
                // nums[i]-j<=nums[i-1]-k => k<=nums[i-1]-nums[i]+j                    
                for (int k=0; k<=min(j,nums[i-1]-nums[i]+j); k++)
                {
                    dp[i][j] += dp[i-1][k];
                    dp[i][j] %= M;
                }

            }
        int ret = 0;
        for (int j=0; j<=nums[n]; j++)
        {
            ret += dp[n][j];
            ret%=M;
        }

        return ret;

    }
};
