class Solution {
public:
    const int mod = 1e9 + 7;
    long long solve(int ind, int last1, int last2, vector<int>&nums, int n, vector<vector<vector<int>>>&dp){
        if(ind == n) return 1;
        if(dp[ind][last1][last2] != -1) return dp[ind][last1][last2];
        long long ans = 0;
        for(int i=last1;i <= nums[ind]; i++){
            for(int j=last2; j >= 0; j--){
                if((i+j) == nums[ind]){
                    ans += solve(ind + 1 , i , j , nums , n , dp);
                    ans %= mod;
                }
            }
        }
        return dp[ind][last1][last2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        long long res = 0;
        vector<vector<vector<int>>>dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        res = solve(0 , 0 , nums[0] , nums , n , dp);
        int ans = res%mod;
        return ans;        
    }
};
