class Solution {
public:
    int pm = 1e9+7 ; 
    int n ; 
    int dp[2020][51] ;

    int add(int a , int b){
        int ans = (a%pm + b%pm)%pm ; 
        if(ans > pm){
            ans%= pm ; 
        }
        if(ans < 0){
            ans += pm ; 
        }
        return ans ; 
    }

    int rec(int level , int last , vector <int> &arr){
        if(level == n){
            return 1 ; 
        }
        if(dp[level][last] != -1){
            return dp[level][last] ; 
        }
        int ans = 0 ; 
        for(int i = last ; i <= 50 ; i++){
            if(i >= last && ((arr[level] - i) <= (arr[level-1] - last)) && ((arr[level] - i) >= 0)   ){
            ans = add(ans , rec(level +1 , i , arr)) ; 
            }
        }
        return dp[level][last] = ans ;
    }



    int countOfPairs(vector<int>& arr) {
        n = (int)arr.size() ; 
        memset(dp , -1 , sizeof(dp)) ;
        int ans = 0 ; 
        for(int i = 0 ; i <= 50 ; i++){
            if(i <= arr[0] && ((arr[0] - i) >= 0) ){
            ans = add(ans , rec(1 , i , arr)) ; 
            }
        }
        return ans  ; 

    }
};
