// no. of arrays with from idx , last entry in first array
int dp[2001][51][51];
const int M = 1e9 + 7;
long long ctpairs(vector<int>& nums, int idx, int next1, int next2){
    if(idx<0) return 1;
    if(dp[idx][next1][next2]!=-1) return dp[idx][next1][next2];
    int n = nums.size();
    long long ans = 0;
    for(int i=0; i<=nums[idx]; i++){
        if(i<=next1 && nums[idx]-i>=next2){
            ans = (ans + ctpairs(nums, idx-1, i, nums[idx]-i))%M;
        }
    }
    return dp[idx][next1][next2] = (int)ans;
}

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        for(int i=0; i<=2000; i++){
            for(int j=0; j<=50; j++){
                for(int k=0; k<=50; k++){
                    dp[i][j][k] = -1;
                }
            }
        }
        return ctpairs(nums, n-1, 50, 0);
    }
};
