class Solution {
    public int countOfPairs(int[] nums) {

        final int MOD = 1_000_000_007;

        int n = nums.length;
        long dp[][][] = new long[n][51][51];

        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i][nums[0]-i] = 1;
        }


        for(int i = 1; i < n; i++) {
            int num = nums[i];
            for(int j = 0; j <= num; j++) {
                int arr1 = j;
                int arr2 = num-j;
                int beforenum = nums[i-1];

                for(int k = 0; k <= beforenum; k++) {
                    int a = k;
                    int b = beforenum-k;
                    if ( a >= arr1 && b <= arr2 ) {
                        dp[i][arr1][arr2] += dp[i-1][a][b];
                        dp[i][arr1][arr2] %= MOD;
                    }
                }




            }
        }

        long result = 0;
        for(int i = 0; i < 51; i++) {
            for(int j = 0; j < 51; j++) {
                result += dp[dp.length-1][i][j];
                result  %= MOD;
            }
        }

        return (int)result;


    }
}
