class Solution {
public:
 int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;

int solve(vector<int>&nums,int ind,int pre1,int pre2){
    if(ind==nums.size()){
        return 1;
    }
      if (dp[ind][pre1 + 1][pre2] != -1) {
            return dp[ind][pre1 + 1][pre2];
        }
    int ans=0;

    for(int i=0;i<=nums[ind];i++){
        if(i>=pre1 && nums[ind]-i<=pre2){
           ans = (ans + solve(nums, ind + 1, i, nums[ind] - i)) % mod;
        }
    }
    return dp[ind][pre1 + 1][pre2] = ans;
}
    int countOfPairs(vector<int>& nums) {

          dp.resize(nums.size(), vector<vector<int>>(52, vector<int>(52, -1)));
        int ans = solve(nums,0,-1,51);
        return ans;
    }
};
