const int MOD = 1e9 + 7;
class Solution {
public:
    int dp[2001][51];
    vector<int> nums;

    int countPairs(int i, int ibefore) {
        if (i == nums.size()) return 1;
        if (dp[i][ibefore] != -1) return dp[i][ibefore];
        int count = 0;
        for (int u1 = ibefore; u1 <= nums[i]; ++u1) {
            int u2 = nums[i] - u1;
            if (i == 0 || u2 <= nums[i - 1] - ibefore) count = (count + countPairs(i + 1, u1)) % MOD;
        }
        return dp[i][ibefore] = count;
    }
    int countOfPairs(vector<int>& _nums) {
        memset(dp, -1, sizeof(dp));
        nums = _nums;
        return countPairs(0, 0);
    }
};
