class Solution {
public:
    const int MOD = 1e9 + 7;
    int solve(vector<int> &nums, int curr, int p1, int p2, vector<vector<vector<int>>> &dp, int n){
        if (curr == n){
            return 1;
        }

        if(dp[curr][p1][p2] != -1){
            return dp[curr][p1][p2];
        }

        int ans = 0;
        for (int i = p1; i <= nums[curr]; i++) {
            long long j = nums[curr] - i;
            if(j <= p2){
                ans = (ans%MOD + solve(nums, curr + 1, i, j, dp, n)%MOD)%MOD;
            }
        }

        return dp[curr][p1][p2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50, dp, n);
    }
};
