class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @cache
        def check(i, last1, last2):
            if i == n:
                return 1

            answer = 0
            for v in range(last1, nums[i] + 1):
                if nums[i] - v > last2:
                    continue

                answer += check(i + 1, v, nums[i] - v)

            return answer

        return check(0, 0, float('inf')) % (10**9 + 7)
