const int MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
         int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, 0));

        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int a2 = nums[i] - j;
                for (int k = 0; k <= j; ++k) {
                    if (a2 <= nums[i-1] - k) {
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }

        int ans = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            ans = (ans + dp[n-1][j]) % MOD;
        }
        return ans;
    }
};
