func countOfPairs(nums []int) int {
    dp := make([][]int, 51)

    for i := range dp {
        dp[i] = make([]int, len(nums))
    }

    for i := 0; i <= nums[0]; i ++ {
        dp[i][0] = 1
    }

    for index := 1; index < len(nums); index ++ {
        val := nums[index]
        for i := 0 ; i <= val ; i ++ {
            sum := 0
            for j := 0; j <= i ;j ++ {
                if (nums[index - 1] - j) >= (nums[index] - i) {
                    sum += (dp[j][index - 1]) % 1000000007
                    sum = sum % 1000000007
                }
            }
            dp[i][index] = sum % 1000000007
        }

    }
    sum := 0

    for i := 0; i <= nums[len(nums) - 1] ; i ++ {
        sum += dp[i][len(nums) - 1] % 1000000007
    }
    return sum % 1000000007

}
