class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        @lru_cache(None)
        def fun(idx,low,high):
            if idx == len(nums):
                return 1
            ans = 0
            for i in range(nums[idx]+1):
                if i >= low and nums[idx] - i <= high:
                    ans += fun(idx+1,i,nums[idx]-i)
                    ans %= mod
            return ans
        return fun(0,0,51) % mod
