int dp[2001][52][52];

class Solution {
    const int mod = 1e9+7;
    vector<int> v;
    int solve(int ind, int n, int inc, int prev){
        if(ind == n) return 1;
        if(dp[ind][inc][prev] != -1) return dp[ind][inc][prev];
        long long tot = 0;
        for(int i=inc; i<=v[ind]; i++){
            if(v[ind] - i <= prev){
                tot += solve(ind+1, n, i, v[ind]-i);
                tot %= mod;
            }
        }
        return dp[ind][inc][prev] = (int)tot;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        this->v = nums;
        memset(dp, -1, sizeof(dp));
        return solve(0, n, 0, 51);
    }
};
