class Solution {
public:
    long long int solve(int index, int temp, int temp1, vector<int> &nums, vector<vector<vector<long long int>>> &arr) {
        if (index >= nums.size()) {
            return 1;
        }

        long long int ans = 0;
        long long int mod = 1e9 + 7;

        if (arr[index][temp][temp1] != -1) {
            return arr[index][temp][temp1];
        }

        for (int i = temp; i <= nums[index]; i++) {
            for (int j = 0; j <= temp1; j++) {
                if (i + j == nums[index]) {
                    ans += solve(index + 1, i, j, nums, arr);
                    ans = ans % mod;
                }
            }
        }

        return arr[index][temp][temp1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long int>>> arr(n, vector<vector<long long int>>(51, vector<long long int>(51, -1)));
        return solve(0, 0, nums[0], nums, arr);
    }
};
