class Solution {
public:
    vector<vector<long long>> dp;
    long long helper(vector<int> &nums, int i, int prev){
        if(i==nums.size()){
            return (long long)1;
        }
        if(prev!=-1 && dp[i][prev]!=-1){
            return dp[i][prev];
        }

        long long res=0;
        if(prev==-1){
            for(int j=0; j<=nums[i]; j++){
                res += helper(nums,i+1,j)%(long long)(1e9+7);
            }
        }else{
            for(int j=prev; j<=nums[i]; j++){
                if((nums[i]-j)>=0 && (nums[i]-j)<=(nums[i-1]-prev)){
                    res += helper(nums,i+1,j)%(long long)(1e9+7);
                }
            }
        }
        if(prev!=-1){
            dp[i][prev]=res%(long long)(1e9+7);
        }
        return res%(long long)(1e9+7);
    }

    int countOfPairs(vector<int>& nums) {

        int n = nums.size();
        dp.resize(n,vector<long long>(51,-1));

        return helper(nums,0,-1)%(long long)(1e9+7);
    }
};
