class Solution {
    public int countOfPairs(int[] nums) {
        int[][] dp = new int[nums.length][51];
        int mod = (int)(1e9 + 7);

        for(int i = 0; i < 51; i++) {
            if(i > nums[0]) break;
            dp[0][i] = 1;
        }

        for(int i = 1; i < nums.length; i++) {
            for(int j = 0; j < 51; j++) {
                if(j > nums[i]) break;
                for(int k = 0; k <= j; k++) {
                    if((nums[i - 1] - k) >= (nums[i] - j)) {
                        dp[i][j] += dp[i - 1][k] % mod;
                        dp[i][j] = dp[i][j] % mod;
                    }
                }
            }
        }

        int total = 0;
        for(int i = 0; i < 51; i++) {
            total += dp[nums.length - 1][i];
            total = total % mod;
        }
        return total;
    }
}
