import java.util.Arrays;

class Solution {
    int dp[][][];
    int mod = (int)1e9 + 7;

    public int countOfPairs(int[] nums) {
        dp = new int[51][51][nums.length+1];
        for(int[][] i : dp) {
            for(int[] j : i) {
                Arrays.fill(j, -1);
            }
        }
        return solve(0, 50, 0, nums);
    }

    public int solve(int i, int d, int ind, int[] nums) {
        if(ind >= nums.length) return 1;
        if(dp[i][d][ind] != -1) return dp[i][d][ind];

        int ans = 0;
        for(int num = 0; num <= nums[ind]; num++) {
            int inc = num;
            int dc = nums[ind] - inc;

            if(inc >= i && dc <= d) {
                ans = (ans + solve(inc, dc, ind + 1, nums)) % mod;
            }
        }

        return dp[i][d][ind] = ans;
    }
}
