class Solution {
    int mod = 1000000007;
    public int countOfPairs(int[] nums) {
        int max = 50;
        int n = nums.length;
        int[][] dp = new int[n][max + 1];
        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }
        for(int i = 1; i < n; i++) {
            for(int j = 0; j <= max; j++) {
                if(dp[i - 1][j] == 0) {continue;}
                int lower = Math.max(j, j + nums[i] - nums[i - 1]);
                int upper = nums[i];
                for(int k = lower; k <= upper; k++) {
                    dp[i][k] += dp[i - 1][j];
                    dp[i][k] %= mod;
                }
            }
        }
        int res = 0;
        for(int i = 0; i <= max; i++) {
            res += dp[n - 1][i];
            res %= mod;
        }
        return res;
    }
}
