#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& v) {
        int n = v.size();
        vector<vector<int>> dp(n, vector<int>(51));
        for (int i = 0; i <= v[0]; i++)
            dp[0][i] = 1;
        for (int i = 0; i + 1 < n; i++)
            for (int j = 0; j <= 50; j++)
                for (int k = j; k <= v[i + 1]; k++)
                    if (v[i] - j >= v[i + 1] - k)
                        (dp[i + 1][k] += dp[i][j]) %= MOD;
        int ans = 0;
        for (int i : dp.back())
            (ans += i) %= MOD;
        return ans;
    }
};
