class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def f(i, l, u):
            if i == len(nums):
                return 1

            pairs = 0
            for j in range(l, nums[i] + 1):
                if nums[i] - j <= u:
                    pairs = (pairs + f(i + 1, j, nums[i] - j)) % MOD

            return pairs

        return f(0, 0, nums[0])
