class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def bf(i, a1, a2):
            if i >= len(nums):
                return 1
            n = nums[i]
            s = 0
            for k in range(n+1):
                if a1 <= k and a2 >= (n-k):
                    s += bf(i+1, k, n-k)
            return s

        return bf(0, 0, math.inf) % (10**9 + 7)
