class Solution {
    public int countOfPairs(int[] nums) {
      dp = new long[nums.length][51][51];
      for (long[][] g : dp)
        for (long[] r : g) Arrays.fill(r, -1);
      long ans = count(nums, 0, 0, 50);
      return (int)ans;
    }

    long[][][] dp;
    int mod = (int)1e9+7;

    long count(int[] nums, int i, int f, int s) {
        if (i == nums.length) return 1;
        long ans = 0;
        if (dp[i][f][s] != -1) return dp[i][f][s];
        for (int x = f; x <= nums[i]; x++) {
            if (nums[i] - x <= s) {
              long v = count(nums, i + 1, x, nums[i] - x);
              ans = (ans + v) % mod;
            }
        }
        dp[i][f][s] = ans;
        return ans;
    }


}
