class Solution {
public:
    int mod =1e9+7;
    int solve(int ind,int prev,vector<int>& nums,vector<vector<int>> &dp){
        if(ind==nums.size()) return 1;

        if(dp[ind][prev]!=-1) return dp[ind][prev];
        int ans = 0;

        for(int i=prev;i<=nums[ind];i++){
            if(ind>0 && nums[ind-1]-prev>=nums[ind]-i){
                ans = (ans + solve(ind+1,i,nums,dp))%mod; 
            }else if(ind==0){
                ans = (ans + solve(ind+1,i,nums,dp))%mod;
            }
        }
        return dp[ind][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n,vector<int>(55,-1));
        return solve(0,0,nums,dp);
    }
};
