class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(int i, int prev1, int prev2, const std::vector<int>& nums) {
        int n = nums.size();
        if (i == n) return 1;

        if (dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];

        int count = 0;
        for (int j = 0; j <= nums[i]; ++j) {
            int newArr1 = j;
            int newArr2 = nums[i] - j;

            if (prev1 <= newArr1 && prev2 >= newArr2) {
                count = (count + solve(i + 1, newArr1, newArr2, nums)) % MOD;
            }
        }

        return dp[i][prev1][prev2] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        dp.assign(n, std::vector<std::vector<int>>(51, std::vector<int>(51, -1)));

        return solve(0, 0, nums[0], nums);
    }
};
