class Solution
{
public:
    const int mod = 1e9 + 7;
    vector<int> arr;
    int memo[2001][51][51];
    int helper(int i, int a1, int a2)
    {
        if (i == arr.size())
            return 1;

        if (memo[i][a1][a2] != -1)
            return memo[i][a1][a2];

        int result = 0;
        for (int i1 = a1; i1 <= arr[i]; i1++)
            if (arr[i] <= a2 + i1)
                result = (result + helper(i + 1, i1, arr[i] - i1)) % mod;

        return memo[i][a1][a2] = result;
    }

    int countOfPairs(vector<int> &arr_)
    {
        arr = arr_;
        memset(memo, -1, sizeof(memo));
        return helper(0, 0, 50);
    }
};
