class Solution {
public:
    int solve(vector<int> &nums,int i,int p,vector<vector<int>> &dp){
        if(i==nums.size()) return 1;
        int x=0,mod=1e9+7;
        if(dp[i][p]!=-1) return dp[i][p];
        for(int j=0;j<=nums[i];j++){
            if(p<=j&&nums[i-1]-p>=nums[i]-j){
                x+=solve(nums,i+1,j,dp);
                x=x%mod;
            }
        }
        x=x%mod;
        return dp[i][p]=x;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size(),vector<int>(52,-1));
        int ans=0,mod=1e9+7;
        for(int i=0;i<=nums[0];i++){
            ans+=solve(nums,1,i,dp);
            ans=ans%mod;
        }
        return ans;
    }
};
