class Solution {
public:
    const int M=1e9+7;
    int dp[2001][52][52];
    int solve(int ind, int prev1, int prev2, vector<int> &nums, int n){
        if(ind==n){
            return 1;
        }
        if(dp[ind][prev1][prev2]!=-1){
            return dp[ind][prev1][prev2];
        }
        int lb=prev1;
        int ub=nums[ind];
        int ans=0;
        for(int i=lb; i<=ub; i++){
            int one=i;
            int two=nums[ind]-one;
            if(two<=prev2){
                ans=(ans%M + solve(ind+1,one,two,nums,n)%M)%M;
            }
        }
        return dp[ind][prev1][prev2]=ans%M;;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        int ans=solve(0,0,nums[0],nums,n);
        return ans;
    }
};
