class Solution {
public:
    int dp[2001][52][52];
    const int mo = 1e9 + 7;
    int sol(int i, int pre1,int pre2, int n, vector<int>&nums){
        if(i >= n) return 1;
        if(dp[i][pre1 + 1][pre2 + 1] != -1) return dp[i][pre1 + 1][pre2 + 1];
        long long ans = 0;
        for(int j=pre1; j<=50; j++){
            if(nums[i] - j >= 0 && (nums[i] - j <= pre2 || pre2 == -1)){
                ans += sol(i+1, j, nums[i] - j, n, nums);
                ans = ans % mo;
            }
        }
        return dp[i][pre1 + 1][pre2 + 1] = ans % mo;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return sol(0, 0, -1, n, nums);
    }
};
