#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<ll>>> memo(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(values, 0, 0, values[0], memo);
    }

private:
    ll solve(const vector<int>& values, int index, int val1, int val2, vector<vector<vector<ll>>>& memo) {
        if (index >= values.size()) return 1;
        ll result = 0;
        ll mod_val = 1e9 + 7;
        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];
        result = countPairs(values, index, val1, val2, memo);
        return memo[index][val1][val2] = result % mod_val;
    }

    ll countPairs(const vector<int>& values, int index, int val1, int val2, vector<vector<vector<ll>>>& memo) {
        ll result = 0;
        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result += solve(values, index + 1, i, j, memo);
                }
            }
        }
        return result;
    }
};
