class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<long long>>dp;

    int countOfPairs(vector<int>& nums) {
        int max_val = *max_element(nums.begin(), nums.end());
        int n = nums.size();

        dp.resize(n, vector<long long>(max_val + 1, -1));

        long long ans = 0;
        for (int j = 0; j <= min(max_val, nums[0]); ++j) {
            ans += helper(0, j, nums[0] - j, nums, n);
            ans %= MOD;
        }

        return static_cast<int>(ans);
    }

private:
    long long helper(int i, int a, int b, const vector<int>& nums, int n) {
        // poora khatam kr diya
        if (i == n - 1) {
            return 1;
        }

        if (dp[i][a] != -1) return dp[i][a];

        long long res = 0;
        for (int k = a; k <= nums[i + 1]; ++k) {
            int next2 = nums[i + 1] - k;
            if (next2 <= b) {
                res += helper(i + 1, k, next2, nums, n);
                res %= MOD;
            }
        }

        dp[i][a] = res;
        return res;
    }

};
