class Solution {
    const int MOD = 1e9 + 7;
public:
    int rec(int i, vector<int>& nums, int prev1, int prev2, vector<vector<vector<int>>>& dp) {
        int n = nums.size();
        if (i == n) {
            return 1;
        }
        if (dp[i][prev1][prev2] != -1) {
            return dp[i][prev1][prev2];
        }

        int ans = 0;
        for (int j = 0; j <= nums[i]; j++) {
            if (j >= prev1 && nums[i] - j <= prev2) {
                ans = (ans + rec(i + 1, nums, j, nums[i] - j, dp)) % MOD;
            }
        }
        return dp[i][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return rec(0, nums, 0, 50, dp);
    }
};
