class Solution {
public:
    int mod=1e9+7;
    long long int dp[2001][51];

    long long int solve(int i,int last,vector<int>&nums)
    {
        if(i>=nums.size())
        {
            return 1;
        }

        if(dp[i][last]!=-1)
        {
            return dp[i][last];
        }

        long long int ans=0;
        if(i==0)
        {
            for(int st=0;st<=nums[i];st++)
            {
                ans=(ans+solve(i+1,st,nums))%mod;
            }
        }

        else
        {
            for(int st=last;st<=nums[i];st++)
            {
                int last2=nums[i-1]-last;
                if(last2>=nums[i]-st){
                    ans=(ans+solve(i+1,st,nums))%mod;
                }

            }
        }

        return dp[i][last]=ans%mod;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,0,nums)%mod;
    }
};
