class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n=a.size();
       int dp[n][51][51];
       memset(dp,0,sizeof(dp));
       for(int i=0;i<=a[0];i++){
        dp[0][i][a[0]-i]=1;
       }
       int MOD=1e9+7;
       for(int i=1;i<n;i++){
        for(int j=0;j<=50;j++){
            for(int k=0;k<=50;k++){
               if(j>a[i])continue;
               if(k>a[i-1])continue;
               if(a[i-1]-k>j)continue;
               if(a[i]-j>k)continue;
               dp[i][j][a[i]-j]+=dp[i-1][a[i-1]-k][k];
               dp[i][j][a[i]-j]%=MOD;
            }
        }
       }
       int ans=0;
       for(int i=0;i<=50;i++){
        for(int j=0;j<=50;j++){
            ans+=dp[n-1][i][j];
            ans%=MOD;
        }
       } 
        return ans;
    }
};
