class Solution {
public:
const int mod=1000000007;
int dp[2001][53][53];
long long f(vector<int>&nums,int index,int prev1,int prev2){
    if(index>=nums.size()){
        return 1;
    }
    if(dp[index][prev1+1][prev2+1]!=-1){
        return dp[index][prev1+1][prev2+1];
    }
    long long ans=0;
    int curr=nums[index];
    for(int i=0;i<=curr;i++){
        int curr1=i;
        int curr2=nums[index]-i;
        if((prev1==-1&&prev2==-1)||(curr1>=prev1&&prev2>=curr2)){
ans=(ans+f(nums,index+1,curr1,curr2)%mod)%mod;
        }
    }
    return dp[index][prev1+1][prev2+1]=ans%mod;
}
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int index=0;
        int prev1=-1;
        int prev2=-1;
        memset(dp,-1,sizeof(dp));
        long long ans=f(nums,index,prev1,prev2)%mod;
        return (int)ans;
    }
};
