class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        int n=nums.size();
        long long int prime=1e9+7;

        //first value
        vector<long long int> DP(51,0);

        for(int val1=0;val1<=nums[n-1];val1++)
        {
            DP[val1]=1;
        }

        for(int i=n-2;i>=0;i--)
        {
            vector<long long int> newDP(51,0);
            for(int val1=0;val1<=50;val1++)
            {
                int a1=val1;
                int a2=nums[i]-a1;

                for(int nextval1=a1;nextval1<=50;nextval1++)
                {
                    if(nums[i+1]-nextval1>a2)   continue;
                    newDP[val1]+=DP[nextval1];
                }
                newDP[val1]%=prime;
            }

            DP=newDP;
        }

        long long int ans=0;
        for(auto&x:DP)  ans+=x;

        return ans%prime;
    }
};
