class Solution {
public:
const int mod=1e9+7;
int dp[2001][51][51];
    int solve(int idx,int a,int b,int maxi,vector<int>&nums)

    {
        if(idx==nums.size())return 1;
        if(dp[idx][a][b]!=-1)return dp[idx][a][b];
        int ans=0,rg=nums[idx];
        for(int i=0;i<=rg;i++)
        {
            int j=rg-i;
            if((idx>0 && a<=i && b>=j)||idx==0)
            ans=(ans+solve(idx+1,i,j,maxi,nums))%mod;

        }
        return dp[idx][a][b]=(ans%mod);
    }
    int countOfPairs(vector<int>& nums) {
        int  max_val=*max_element(nums.begin(),nums.end());
        memset(dp,-1,sizeof(dp));
       return  solve(0,0,50,max_val,nums)%mod;
    }
};
