class Solution {
public:
    long long mod=1e9+7;
    long long solve(int i,int f,vector<int> &nums,int n,vector<vector<long long>> &dp){
        if(i==n){
            return 1;
        }

        if(dp[i][f]!=-1){
            return dp[i][f];
        }

        long long s=nums[i];
        if(i-1>=0){
            s=nums[i-1]-f;
        }

        long long res=0;
        for(int j=f;j<=nums[i];j++){
            int a=j;
            int b=nums[i]-j;

            if(b<=s){
                res+=solve(i+1,a,nums,n,dp)%mod;
            }
        }

        return dp[i][f]=res%mod;

    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();

        vector<vector<long long>> dp(n+1,vector<long long>(53,-1));
        long long ans=solve(0,0,nums,n,dp);

        return ans;
    }
};
