class Solution {
public:
int dp[2001][55];
int mod=1e9+7;
vector<int> arr;
int n;
int find(int c,int p){

    if(c==n){
        return 1;
    }
if(dp[c][p]!=-1){
    return dp[c][p];
}
int d;
if(c==0){
    d=50;
}else{
d=arr[c-1]-p;
}
int ans=0;
for(int i=p;i<=50;i++){
    int b=arr[c]-i;
    if(i>arr[c]||b>d){
        continue;
    }
    //cout<<c<<" "<<i<<endl;
    ans=(ans+find(c+1,i))%mod;
}
return dp[c][p]=ans;

}
    int countOfPairs(vector<int>& nums) {
       memset(dp,-1,sizeof(dp));
       arr=nums;
       n=arr.size();
      return  find(0,0); 
    }
};
