struct key{
    int pichla1;
    int pichla2;
    int x;
    bool operator==(const key&other) const{
        return pichla1==other.pichla1&&pichla2==other.pichla2&&x==other.x;
    }
};
struct Hash{
    size_t operator()(const key &kk) const {
        return hash<int>()(kk.pichla1)^(hash<int>()(kk.pichla2)<<1)^(hash<int>()(kk.x)<<2);
    }
};
class Solution {
    unordered_map<key,int,Hash>dp;
    int modu = 1e9+7;
    int calc(vector<int>&nums,int pichla1,int pichla2,int i){
        if(i>=nums.size()){

            return 1;
        }
        key key = {pichla1,pichla2,i};
        if(dp.find(key)!=dp.end()){
            return dp[key];
        }
        int ans = 0;
        for(int j = 0;j<=nums[i];j++){
            if(j>=pichla1&&nums[i]-j<=pichla2){
               ans = (ans+ calc(nums,j,nums[i]-j,i+1))%modu;
            }
        }
        dp[key] = ans;
        return ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        return calc(nums,-1,INT_MAX,0);
    }
};
