int mod = 1000000007;
class Solution {
public:
    int dp[2001][51][51];
    int helper(int idx,int firstVal,int secVal,int limit,int n,vector<int>&nums){
        if(idx>=n){
            return 1;
        }
         if(dp[idx][firstVal][secVal]!=-1) return dp[idx][firstVal][secVal];
        int ans = 0;
        for(int i = firstVal;i<=limit;i++){
            for(int j = secVal;j>=0;j--){
                if(i+j==nums[idx]){
                int cur = helper(idx+1,i,j,limit,n,nums);
                ans = (ans%mod + cur %mod) %mod;
                }
            }
        }
        // return ans;
         return dp[idx][firstVal][secVal] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mx = *max_element(nums.begin(),nums.end());
        memset(dp,-1,sizeof(dp));
        return helper(0,0,mx,mx,n,nums);
    }
};
