class Solution {
public:
    int mod = int(1e9 + 7);
    int dp[2001][51];
    int func(int i,int prev,vector<int> &v)
    {
        if(i == v.size()) return 1;
        int ans = 0;

        if(dp[i][prev] != -1) return dp[i][prev];
        for(int j = 0; j <= v[i]; j++)
        {
            if(j >= prev)
            {
                if(i != 0)
                {
                    if(v[i-1] - prev >= (v[i] - j)) ans = (ans + func(i+1,j,v)) % mod;
                }
                else
                    ans = (ans + func(i+1,j,v)) % mod;
            }
        }
        return dp[i][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return func(0,0,nums);
    }
};
