MOD = 10 ** 9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        @lru_cache(None)
        def dp(index, last_arr1, last_arr2):
            if index == n:
                return 1

            total = 0
            for arr1_val in range(last_arr1, nums[index] + 1):
                arr2_val = nums[index] - arr1_val
                if arr2_val <= last_arr2:
                    total += dp(index + 1, arr1_val, arr2_val)
                    total %= MOD

            return total

        return dp(0, 0, 50)  # max value of arr2 is bounded by 50
