class Solution:
    def countOfPairs(self, nums: List[int]) -> int:


        mod = 10 ** 9 + 7

        res = 0
        @cache
        def dp(i, arr1, arr2):
            if i >= len(nums):
                return 1

            total = 0
            for j in range(nums[i] + 1):
                a = nums[i] - j
                b = j


                if arr1 <= a and arr2 >= b:
                    total += dp(i + 1, a, b)


            return total



        return dp(0, -math.inf, math.inf) % mod
