class Solution {
public:
    int mem[2000][51];
    const int m=1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        for(int i=1;i<n;++i)fill(mem[i],mem[i]+51,0);
        for(int j=0;j<=nums[0];++j)mem[0][j]=1;
        for(int i=1;i<n;++i){
            for(int j=0;j<=nums[i];++j){
                for(int k=0;k<=min(j,nums[i-1]-nums[i]+j);++k){
                    mem[i][j]+=mem[i-1][k];
                    if(mem[i][j]>=m)mem[i][j]-=m;
                }
               // cout<<"mem"<<i<<' '<<j<<' '<<mem[i][j]<<endl;
            }
        }
        long long ans=0;
        for(int j=0;j<=nums[n-1];++j)ans+=mem[n-1][j];
        return ans%m;
    }
};
