#define ll long long int

class Solution {
public:
    int solve(int position, int first, int second, vector<vector<vector<ll>>> &dp, vector<int> &nums, int n){
        if(position >= n) return 1;

        ll total = 0;
        ll MOD = 1e9+7;

        if (dp[position][first][second] != -1) return dp[position][first][second];
            for (int x = first; x <= nums[position]; x++) {
                for (int y = second; y >= 0; y--) {
                    if (x + y == nums[position]) {
                        total += solve(position + 1, x, y,dp, nums, n);
                        total = total % MOD;
                    }
                }
            }

        return dp[position][first][second] = total; 

    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));

        int result = solve(0, 0, nums[0], dp, nums, n);

        return result;
    }
};
