class Solution {
public:
    const int MOD = 1e9+7;
    int solve(int idx, int num1, int num2, vector<int> &nums, vector<vector<vector<int>>>& dp){
        if(idx == nums.size()) return 1;

        if(dp[idx][num1][num2] != -1) return dp[idx][num1][num2];

        int res = 0;
        for(int i = num1; i <= nums[idx]; i++){
            if(nums[idx]-i > num2) continue;
            res = (res + solve(idx+1, i, nums[idx]-i, nums, dp))%MOD;
        }
        return dp[idx][num1][num2] = res;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n+1, vector<vector<int>>(51, vector<int>(51, -1)));

        int res = 0;
        for(int i = 0; i <= nums[0]; i++){
            res = (res + solve(1, i, nums[0]-i, nums, dp))%MOD;
        }
        return res;
    }
};
