class Solution {
public:
    long fun(int indx, int prev1, int prev2,vector<int>& nums,vector<vector<vector<long>>>& dp){
        int n = nums.size();

        if(indx == n)   return 1;

        if(dp[indx][prev1][prev2] != -1)    return dp[indx][prev1][prev2];

        long res = 0;
        int mod = 1e9 + 7;

        for(int i = prev1; i <= nums[indx]; i++)
            if(((nums[indx]-i) >= 0) && ((nums[indx]-i) <= prev2))
                res = (res + fun(indx+1,i,nums[indx]-i,nums,dp)) % mod;

        return (dp[indx][prev1][prev2] = res);
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<long>>> dp(n,vector<vector<long>>(51,vector<long>(51,-1)));

        return fun(0,0,50,nums,dp);
    }
};
