class Solution {
public:
    const long long MOD = 1e9 + 7;

    long long countPairsHelper(int index, int minValue, int maxValue, const vector<int>& nums, vector<vector<vector<long long>>>& dp) {
        if (index >= nums.size()) return 1;
        if (dp[index][minValue][maxValue] != -5) return dp[index][minValue][maxValue];

        long long count = 0;
        int lowerBound = minValue;
        while (lowerBound <= nums[index]) {
            int upperBound = maxValue;
            int check=0;
            while (upperBound >= 0) {
                if (lowerBound + upperBound == nums[index]) {
                    count += countPairsHelper(index + 1, lowerBound, upperBound, nums, dp);
                    count %= MOD;
                }
                check++;
                upperBound--;
            }
            if(check<0) lowerBound=upperBound;
            lowerBound++;
        }

        return dp[index][minValue][maxValue] = count;
    }

    int countOfPairs(vector<int>& nums) {
        int length = nums.size();
        vector<vector<vector<long long>>> dp(length, vector<vector<long long>>(51, vector<long long>(51, -5)));
        return countPairsHelper(0, 0, nums[0], nums, dp);
    }
};
