class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(int i, int l, int r, vector<int> &nums) {
        if (i == nums.size()) return 1;
        if (dp[i][l][r] != -1) return dp[i][l][r];

        int ans = 0;
        for (int j = l; j <= nums[i]; j++) {
            int se = nums[i] - j;
            if (se > r) continue;
            ans = (ans + solve(i + 1, j, se, nums)) % MOD;
        }

        return dp[i][l][r] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mx = *max_element(nums.begin(), nums.end());
        dp.assign(n, vector<vector<int>>(mx + 1, vector<int>(mx + 1, -1)));
        return solve(0, 0, nums[0], nums);
    }
};
